package com.bizintelapps.zytoon.validation.impl;

import javax.validation.ConstraintValidator;
import javax.validation.ConstraintValidatorContext;

import com.bizintelapps.zytoon.validation.CheckCase;
import com.bizintelapps.zytoon.validation.CheckCase.CaseMode;

/**
 * Implementation of the example {@link CheckCase} constraint
 * @see CheckCase
 * @See http://docs.jboss.org/hibernate/validator/4.0.1/reference/en/html_single/#validator-customconstraints
 */
public class CheckCaseValidator implements ConstraintValidator<CheckCase, String> {

    private CaseMode caseMode;

    public void initialize(CheckCase constraintAnnotation) {
        this.caseMode = constraintAnnotation.value();
    }

    public boolean isValid(String object, ConstraintValidatorContext constraintContext) {
        if (object == null) {
            return true;
        }

        if (caseMode == CaseMode.UPPER) {
            return object.equals(object.toUpperCase());
        } else {
            return object.equals(object.toLowerCase());
        }
    }
}